const rest = "";

const patterns = {
    '9': '1001',
    '246': '11110110',
    '248': '11111000',
    '8': '1000',
    '13': '1101'
};

for (const [key, pattern] of Object.entries(patterns)) {
    for (let i = 0; i < rest.length - pattern.length; i++) {
        if (rest.substring(i, i + pattern.length) === pattern) {
            console.log(`Found '${key}' at offset ${i}`);
        }
    }
}
